<?phpinclude('../_inc/config.inc');global $mysql;$sql = "SELECT id, project_id, company_id,requested_by,assigned_to FROM `workorders`  ORDER BY `workorders`.`id` DESC  ";$result = $mysql->sqlordie($sql);while($row = $result->fetch_assoc()){		if($row['company_id'] == '0'){		$project_id = $row['project_id'];		$wid = $row['id'];		$sql2 = "SELECT company FROM `projects`  WHERE id = ? ORDER BY `projects`.`id` DESC";		$r = $mysql->sqlprepare($sql2,array($project_id));		$company_row = $r->fetch_assoc();		$company_id = $company_row['company'];		$requested_by = $row['requested_by'];		$assigned_to = $row['assigned_to'];		$update_sql = "UPDATE workorders SET company_id = '$company_id' WHERE id ='$wid'";		$mysql->sqlordie($update_sql);		echo "Workorder ::".$wid." ===::New Company".$company_id."<br/>\n";		//check if company is Mapped with User		if(!empty($requested_by)){			$company_user_sql = "SELECT user_id FROM `users_companies` WHERE user_id = '$requested_by' AND  company_id = '$company_id'";			$count_result = $mysql->sqlordie($company_user_sql);			//if user not mapped with that company			if($count_result->num_rows == 0){								$insert_sql = " INSERT INTO users_companies SET user_id = '$requested_by',company_id = '$company_id' ,deleted= '0', modify_date = now() ";				$mysql->sqlordie($insert_sql);			}		}		if(!empty($assigned_to)){			$company_user_sql = "SELECT user_id FROM `users_companies` WHERE user_id = '$assigned_to' AND  company_id = '$company_id'";			$count_result = $mysql->sqlordie($company_user_sql);			//if user not mapped with that company			if($count_result->num_rows == 0){				$insert_sql = " INSERT INTO users_companies SET user_id = '$assigned_to',company_id = '$company_id' ,deleted= '0', modify_date = now() ";				$mysql->sqlordie($insert_sql);			}				}				}}